let exec = require('child_process').exec;
let env = require('./env');

function enableAirport(config, callback) {
    let commandStr = "networksetup -setairportpower";
    let iface = 'en0';
    if (config.iface) {
        iface = config.iface.toString();
    }

    commandStr = commandStr + " " + iface + " " + 'on'

    exec(commandStr, env, function (err, resp) {
        callback && callback(err);
    });
}

module.exports = function (config) {

    return function(callback) {
      if (callback) {
        enableAirport(config, callback);
      } else {
        return new Promise(function (resolve, reject) {
            enableAirport(config, function (err) {
            if (err) {
              reject(err);
            } else {
              resolve();
            }
          })
        })
      }
    }
}